-- CONSULTA 1 :
SELECT PERSONA.NOMBRE AS NOMBRE, ROL.TIPO AS ROL FROM PERSONA, ROL, ROL_PERSONA 
WHERE P_ID_PERSONA = ROL_PERSONA.P_ID_PERSONA AND ROL.ID_ROL = ROL_PERSONA.R_ID_ROL;
-- CONSULTA 2:
SELECT PERSONA.NOMBRE AS NOMBRE, ROL.TIPO AS ROL, DEPARTAMENTO.NOMBRE_DEPTO FROM PERSONA, ROL, ROL_PERSONA, DEPARTAMENTO 
WHERE P_ID_PERSONA = ROL_PERSONA.P_ID_PERSONA AND ROL.ID_ROL = ROL_PERSONA.R_ID_ROL AND DEPARTAMENTO.ID_DEPARTAMENTO = PERSONA.D_ID_DEPARTAMENTO;
