--MAS CONSULTAS
select nombre, MONTH(fechanacimiento) from PERSONA WHERE MONTH (FECHANACIMIENTO) = 2 ORDER BY (FECHANACIMIENTO)DESC; 
--PARES
select nombre, MONTH(fechanacimiento) from PERSONA WHERE MONTH (FECHANACIMIENTO)%2=0 ;
--AÑO
select nombre, YEAR(fechanacimiento) from PERSONA WHERE YEAR (FECHANACIMIENTO)<=1984 ORDER BY (FECHANACIMIENTO)DESC; 

--CONSULTA MOSTRAR NOMBRE DE LA PERSONA E INDIQUE SU EDAD ACTUAL :
SELECT PERSONA.NOMBRE, PERSONA.FECHANACIMIENTO, (CAST(DATEDIFF(DD,FECHANACIMIENTO,GETDATE())/365 AS INT)) AS EDAD FROM PERSONA; 

